mapper.select:
select count(board_id) from msa_board.board where board_is_delete = 0
and board_region = #{searchText}
and board_title like CONCAT('%', #{searchText}, '%')

select board_id from board where board_is_delete = 0
and board_region = #{searchText}
and board_title like CONCAT('%', #{searchText}, '%')
order by board_created_at desc limit 5 offset #{offset}

select b.board_id, b.member_id, b.board_title, b.board_content, b.board_region, b.board_view, b.board_updated_at, b.board_created_at, bs.board_star_type, bs.board_star_short_review, bs.board_star_rating from board b join board_star bs on b.board_id = bs.board_id where b.board_is_delete = 0 and b.board_id in
#{arr}
order by bs.sort_no

mapper.update:
update msa_board.board
SET board_title = #{boardTitle},
    board_region = #{boardRegion},
    board_content = #{boardContent}
WHERE board_id = #{boardId};

update board
set board_is_delete = 1
where board_id = #{boardId}

mapper.select:
select exists(select 1
              from board
              where board_id = #{boardId});
